mapper.update:
UPDATE review
  SET
    review_content = #{review_content}
  WHERE
    review_idx = #{review_idx}

mapper.delete:
DELETE
  FROM reply
  WHERE
    review_idx = #{review_idx}
    AND
    reply_re_ref = #{reply_re_ref}

mapper.select:
SELECT COUNT(*)
  FROM style_like_list
  WHERE 
    review_idx = #{review_idx}
    AND
    member_idx = #{member_idx}

mapper.delete:
DELETE
  FROM style_like_list
  WHERE
    member_idx = #{member_idx}
    AND
    review_idx = #{review_idx}
