mapper.update:
UPDATE auth_info
	SET
		auth_code = #{auth_code}
	WHERE
		member_idx = #{member_idx}

mapper.select:
SELECT count(*) FROM auth_info
WHERE
	member_idx = #{member_idx}
	AND
	auth_code = #{auth_code}

SELECT member_id FROM member 
	WHERE 
		member_name=#{member_name} 
		AND 
		member_phone=#{member_phone}
